import React, { Component } from "react";
class PhotoLibrary extends Component {
  state = {
    key: "fda8922284b87b4f38775239ea65f173",
    secret: "60f4b8039d57b683",
    photos: <React.Fragment></React.Fragment>
  };
  fetchImages(pageNumber) {
    fetch(
      "https://www.flickr.com/services/rest/?method=flickr.galleries.getPhotos&api_key=fda8922284b87b4f38775239ea65f173&gallery_id=66911286-72157647277042064&page=" +
        pageNumber +
        "&format=json&nojsoncallback=1"
    )
      .then(data => data.json())
      .then(data => {
        const photos = data.photos.photo.map(obj => {
          const imageUrl =
            "https://farm" +
            obj.farm +
            ".staticflickr.com/" +
            obj.server +
            "/" +
            obj.id +
            "_" +
            obj.secret +
            "_z.jpg?zz=1";
          obj.imageUrl = imageUrl;
          return obj;
        });
        console.log(photos);
        let displayHtml = (
          <div
            id="carouselExampleIndicators"
            className="carousel slide"
            data-ride="carousel"
          >
            <ol className="carousel-indicators">
              {photos.map((obj, index) => {
                if (index === 0) {
                  return (
                    <li
                      key={index}
                      data-target="#carouselExampleIndicators"
                      data-slide-to={index}
                      className="active"
                    ></li>
                  );
                } else {
                  return (
                    <li
                      key={index}
                      data-target="#carouselExampleIndicators"
                      data-slide-to={index}
                    ></li>
                  );
                }
              })}
            </ol>
            <div className="carousel-inner">
              {photos.map((obj, index) => {
                if (index === 0) {
                  return (
                    <div className="carousel-item active" key={obj.id}>
                      <img
                        className="d-block w-100"
                        src={obj.imageUrl}
                        alt={obj.title}
                      />
                    </div>
                  );
                } else {
                  return (
                    <div className="carousel-item" key={obj.id}>
                      <img
                        className="d-block w-100"
                        src={obj.imageUrl}
                        alt={obj.title}
                        width="640px"
                        height="640px"
                      />
                    </div>
                  );
                }
              })}
            </div>
            <a
              className="carousel-control-prev"
              href="#carouselExampleIndicators"
              role="button"
              data-slide="prev"
            >
              <span
                className="carousel-control-prev-icon"
                aria-hidden="true"
              ></span>
              <span className="sr-only">Previous</span>
            </a>
            <a
              className="carousel-control-next"
              href="#carouselExampleIndicators"
              role="button"
              data-slide="next"
            >
              <span
                className="carousel-control-next-icon"
                aria-hidden="true"
              ></span>
              <span className="sr-only">Next</span>
            </a>
          </div>
        );

        this.setState({ photos: displayHtml });
      });
  }
  handleClick = event => {
    event.preventDefault();
    this.fetchImages(10);
  };
  render() {
    return (
      <div>
        <button
          className="btn btn-sm btn-primary m-2"
          onClick={this.handleClick}
        >
          Fetch Images
        </button>
        <br />
        {this.state.photos}
      </div>
    );
  }
}

//https://farm9.staticflickr.com/8187/8432423659_dd1b834ec5.jpg
//https://farm{farm-id}.staticflickr.com/{server-id}/{id}_{secret}.jpg

export default PhotoLibrary;
